SELECT

FROM resultdata_factory rf


    AND rf.createTime > #{startTime,jdbcType=TIMESTAMP}


    AND rf.createTime < #{endTime,jdbcType=TIMESTAMP}

ORDER BY rf.createTime DESC limit #{startPos},#{pageSize}

SELECT COUNT(*) FROM resultdata_factory rf


    AND rf.createTime > #{startTime,jdbcType=TIMESTAMP}


    AND rf.createTime 	< #{endTime,jdbcType=TIMESTAMP}

SELECT

FROM resultdata_factory rf
LEFT JOIN basicinformation bf ON rf.basiciformationId = bf.id


    AND rf.createTime > #{startTime,jdbcType=TIMESTAMP}


    AND rf.createTime < #{endTime,jdbcType=TIMESTAMP}

ORDER BY rf.basiciformationId ASC limit #{startPos},#{pageSize}

