mapper.select:
SELECT

FROM trade t

ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

SELECT
FROM trade t
and ((t.status = 1 and t.is_bond = 1) or (t.status != 0 and t.status != 1 and t.status != 41)) ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

SELECT u.nick AS sellerName, a.nick AS buyersName, b.bft_name,b.bft_size, b.bid_bond as bidBond,

FROM trade t LEFT JOIN `user` u ON t.seller_id = u.id
LEFT JOIN `user` a ON t.buyers_id = a.id
LEFT JOIN bid b ON t.bid_id = b.id


    and b.bft_name LIKE CONCAT('%',#{obj.bftName,jdbcType=VARCHAR},'%')


    and a.nick LIKE CONCAT('%',#{obj.buyersName,jdbcType=VARCHAR},'%')


    and u.nick LIKE CONCAT('%',#{obj.sellerName,jdbcType=VARCHAR},'%')


    and t.transaction_date > #{startDateTime,jdbcType=TIMESTAMP}


    and t.transaction_date < #{endDateTime,jdbcType=TIMESTAMP}

ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

SELECT COUNT(*) FROM trade t LEFT JOIN `user` u ON t.seller_id = u.id LEFT JOIN `user` a ON t.buyers_id = a.id LEFT JOIN bid b ON t.bid_id = b.id
and b.bft_name LIKE CONCAT('%',#{obj.bftName,jdbcType=VARCHAR},'%')
and a.nick LIKE CONCAT('%',#{obj.buyersName,jdbcType=VARCHAR},'%')
and u.nick LIKE CONCAT('%',#{obj.sellerName,jdbcType=VARCHAR},'%')
and t.transaction_date > #{startDateTime,jdbcType=TIMESTAMP}
and t.transaction_date < #{endDateTime,jdbcType=TIMESTAMP}

SELECT u.nick AS sellerName, a.nick AS buyersName,b.bft_name,b.bft_size,li.company_name, b.bid_bond as bidBond,
FROM trade t LEFT JOIN bid b ON t.bid_id = b.id LEFT JOIN `user` u ON t.seller_id = u.id LEFT JOIN `user` a ON t.buyers_id = a.id LEFT JOIN logistics_information li ON t.id = li.trade_id
ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

