mapper.select:
SELECT

FROM trade t

ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

SELECT u.uname AS sellerName, a.uname AS buyersName, b.bft_name,b.bft_size,

FROM trade t LEFT JOIN `user` u ON t.seller_id = u.id
LEFT JOIN `user` a ON t.buyers_id = a.id
LEFT JOIN bid b ON t.bid_id = b.id


    and t.transaction_date > #{startDateTime,jdbcType=TIMESTAMP}


    and t.transaction_date < #{endDateTime,jdbcType=TIMESTAMP}

ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

SELECT COUNT(*) FROM trade t LEFT JOIN bid b ON t.bid_id = b.id


    and t.transaction_date > #{startDateTime,jdbcType=TIMESTAMP}


    and t.transaction_date < #{endDateTime,jdbcType=TIMESTAMP}

SELECT b.bft_name,b.bft_size,li.company_name,
FROM trade t LEFT JOIN bid b ON t.bid_id = b.id LEFT JOIN logistics_information li ON t.id = li.trade_id
ORDER BY t.transaction_date DESC limit #{startPos},#{pageSize}

mapper.update:
UPDATE `trade` SET

    status = #{status,jdbcType=VARCHAR}


    end_date = #{endDate,jdbcType=TIMESTAMP}

WHERE id = #{id,jdbcType=INTEGER}
